const linkBuy=document.querySelector(".button-buy"),modalBuy=document.querySelector(".modal-add"),closeBuy=modalBuy.querySelector(".button-close");linkBuy.addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.toggle("modal-hidden"),modalBuy.classList.toggle("modal-show")}),closeBuy.addEventListener("click",function(e){e.preventDefault(),modalBuy.classList.toggle("modal-show"),modalBuy.classList.toggle("modal-hidden")}),window.addEventListener("keydown",function(e){27===e.keyCode&&modalBuy.classList.contains("modal-show")&&(e.preventDefault(),modalBuy.classList.remove("modal-show"),modalBuy.classList.add("modal-hidden"))});
